INSERT INTO dbo.[IHS_TRACKER] (name, rows_copied)
VALUES('@{pipeline().parameters.Year}-@{pipeline().parameters.Direction}-United States', @{activity('ExtractIHSMarkitProducts').output.rowsCopied});

SELECT SUM(rows_copied) AS total_rows_copied, MIN(created_at) AS oldest_timestamp_within_hour
FROM dbo.[IHS_TRACKER]
WHERE created_at >= DATEADD(Hour, -1, GETDATE());

